import React from "react";
// import "../../assets/style.css";

const Info = () => {
  $(document).ready(function () {
    const img = document.createElement("img");
    let dropdownImg1 = img.attributes("src", "../images/dropdown.png");
    let dropdownImg2 = img.attributes("src", "../images/dropdown.png");
    let dropdownImg3 = img.attributes("src", "../images/dropdown.png");
    let skills = document.getElementById("dropdown1");
    let education = document.getElementById("#dropdown2");
    let projects = document.getElementById("#dropdown3");

    let list1 = $(".hide1");
    list1.toggleClass("hideList");

    let list2 = $(".hide2");
    list2.toggleClass("hideList");

    let list3 = $(".hide3");
    list2.toggleClass("hideList");

    dropdownImg1.attr("class", "dropdown");
    dropdownImg1.toggleClass("showArrow");

    dropdownImg2.attr("class", "dropdown");
    dropdownImg2.toggleClass("showArrow");

    dropdownImg3.attr("class", "dropdown");
    dropdownImg3.toggleClass("showArrow");

    skills.prepend(dropdownImg1);
    education.prepend(dropdownImg2);
    projects.prepend(dropdownImg3);

    skills.on("click", function () {
      if (".showArrow") {
        dropdownImg1.toggleClass("showArrow");
        dropdownImg1.toggleClass("hideArrow");
        list1.toggleClass("showList");
        list1.toggleClass("hideList");
      } else {
        dropdownImg1.toggleClass("hideArrow");
        dropdownImg1.toggleClass("showArrow");
        list1.toggleClass("hideList");
        list1.toggleClass("showList");
      }
    });

    education.on("click", function () {
      if (".showArrow") {
        dropdownImg2.toggleClass("showArrow");
        dropdownImg2.toggleClass("hideArrow");
        list2.toggleClass("showList");
        list2.toggleClass("hideList");
      } else {
        dropdownImg2.toggleClass("hideArrow");
        dropdownImg2.toggleClass("showArrow");
        list2.toggleClass("hideList");
        list2.toggleClass("showList");
      }
    });
  });

  return (
    <div className="info">
      <p>
        nickolasezell@gmail.com <span id="decorate">|</span>{" "}
        <a
          href="https://linkedin.com/in/nick-ezell"
          target="_blank"
          rel="noopener noreferrer"
        >
          <img
            alt="LinkedIn"
            src="./LI-In-Bug.png"
            id="linkedIn"
            className="make-it-slow"
          ></img>
          https://linkedin.com/in/nick-ezell
        </a>
        <span id="decorate">|</span>{" "}
        <a
          href="https://github.com/nick-ezell"
          target="_blank"
          rel="noopener noreferrer"
        >
          <img
            alt="GitHub"
            src="./GitHub-Mark-32px.png"
            id="gitHub"
            className="make-it-slow"
          ></img>{" "}
          https://github.com/nick-ezell
        </a>
      </p>
      <br />
      <p>
        Programmer with strong front-end skills, a background in customer
        service and an eye for aesthetics in design. I have a strong sense of
        self-management as well as a deep-seated desire for personal
        development. Coding was a hobby (in my early youth) that I have recently
        rediscovered, and reinvigorated, my passion for. A combination of
        technical expertise and emotional intelligence allows me to be a unique
        team member that will bring efficiency and value to your team; coupled
        with experience in collaborative, deadline-driven environments make me a
        strong addition to any data analysis team. I seek a role in web
        development at a company where I can learn and continue my journey to
        master the ever-changing craft of code.
      </p>
      <hr />
      <section>
        <h2 id="dropdown1">Skills</h2>
      </section>
    </div>
  );
};

export default Info;
